/* Modulos de librerías */

/* Componentes */
import { Link } from "react-router-dom";
import { Button, InputField, LinkWrapper } from '../../Components';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser } from "@fortawesome/free-regular-svg-icons";

/* Estilos */
import styles from "./Login.module.css";


function Login() {
  return (
    <main className={styles["main-container"]}> {/* FIX: La propia plantilla tiene un main, mover este main y su estilo para que no haya redundancia */}
      <section className={styles["left-section"]}>
        <h1>Inicia sesión</h1>
        <form method="POST" action="">
          <InputField id="user" type="text" label="Nombre de usuario" name="user" placeholder="Usuario"/>
          <InputField id="password" type="password" label="Contraseña" name="password" placeholder="Contraseña"/>
          <Link to="" className={styles["forgot-password-link"]}>¿Has olvidado tu contraseña?</Link>
          <div className={styles["button-container"]}>
            <Button type="reset" />
            <Button type="submit" />

            <FontAwesomeIcon icon={faUser} style={{ fontSize: "2em", color: "white" }} />

          </div>
        </form>
      </section>
      <section className={styles["right-section"]}>
        <img alt="logo" src="/atom.png"/>
        <h1>Bienvenido</h1>
        <p>¿No tienes una cuenta?</p>
        <Link to="" className={styles["register-link"]}>Regístrate</Link>
      </section>
    </main> 
  );
}

export default Login;
